usage:
import Components

// Basic square checkbox
let checkbox = CheckboxView()
checkbox.configure(
    text: "Accept terms",
    shape: .square,
    isChecked: false,
    isEnabled: true,
    checkedIcon: myCheckedIcon,
    colorScheme: .default
)
checkbox.onTap = { print("Toggled: \(checkbox.isChecked)") }

// Circle checkbox without text
let radio = CheckboxView()
radio.configure(
    shape: .circle,
    isChecked: true,
    isEnabled: true,
    checkedIcon: myCircleCheckedIcon
)

// Programmatic toggle with animation
checkbox.toggleChecked(animated: true)
checkbox.setChecked(false, animated: true)
