usage:
// Default chip with icon
let chip = ChipsView()
chip.configure(
    text: "Filter option",
    icon: UIImage(systemName: "person.2"),
    state: .default,
    size: .small
)
chip.onTap = { print("Chip tapped") }

// Active chip
let activeChip = ChipsView()
activeChip.configure(
    text: "Selected",
    icon: UIImage(systemName: "person.2"),
    state: .active,
    size: .medium
)

// Avatar chip with close button
let avatarChip = ChipsView()
avatarChip.configureAvatar(
    name: "John Doe",
    avatarImage: userImage,
    size: .medium
)
avatarChip.onClose = { print("Remove user") }
